import React, { Component } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import queryString from 'query-string';
const pathArray = window.location.pathname.split("/");
const segment_1 = pathArray[2];

class EditNotes extends Component {
    state = {
        quiz1: '25',
        quiz2: '25',
        quiz3: '25',
        perform1: '25',
        perform2: '25',
        project: '25',
    }
    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
        this.calculateAverage();
    }

    async componentDidMount() {
        const params = new URLSearchParams({
            id: segment_1,
          }).toString();
        const res = await axios.post('http://127.0.0.1:8000/api/update', params).then(res => {
            console.log((res.data.data[0]));
            var response = (res.data.data[0]);            
            this.setState({
                quiz1: response.quiz1,
                quiz2: response.quiz2,
                quiz3: response.quiz3,
                perform1: response.perform1,
                perform2: response.perform2,
                project: response.project,
            });
            this.calculateAverage()
          })
          .catch(err => {
            console.log(err);
          });
    }

    checkNotes()
    {
        return true;
    }

    _updateNotes(){
        var quiz1 = this.state.quiz1;
        var quiz2 = this.state.quiz2;
        var quiz3 = this.state.quiz3;
        var project = this.state.project;
        var perform1 = this.state.perform1;
        var perform2 = this.state.perform2;
        var _quiz1=this.state.quiz1;
        var _quiz2=this.state.quiz2;
        var _quiz3=this.state.quiz3;
        var _perform1=this.state.perform1;
        var _perform2=this.state.perform2;
        var _project=this.state.project;

        const params = new URLSearchParams({
            id: segment_1,
            quiz1:_quiz1,
            quiz2:_quiz2,
            quiz3:_quiz3,
            perform1:_perform1,
            perform2:_perform2,
            project:_project
        }).toString();
        axios.post('http://127.0.0.1:8000/api/update', params).then(res => {
            alert(res.data);
        })
        .catch(err => {
        console.log(err);
        });
        
        
    }

    calculateAverage()
    {
        var quiz1 = this.state.quiz1;
        var quiz2 = this.state.quiz2;
        var quiz3 = this.state.quiz3;
        var project = this.state.project;
        var perform1 = this.state.perform1;
        var perform2 = this.state.perform2;

        var total = (parseInt(quiz1) + parseInt(quiz2) + parseInt(quiz3) + parseInt(project) + parseInt(perform1) + parseInt(perform2)) / 6;
        this.setState({totalNotes: total.toFixed(1)});
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <div className="card">
                            <div className="card-header">
                                <h4>Not Bilgileri
                                    <Link to={'/'} className="btn btn-primary btn-sm float-end">Ana Sayfa</Link>
                                </h4>
                                <div className="card-body">
                                    <form onSubmit={this.updateNotes}>
                                        <div className='form-group mb-3'>
                                            <label> 1.Yazılı</label>
                                            <input type="text" name="quiz1" onChange={this.handleInput} value={this.state.quiz1} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 2.Yazılı</label>
                                            <input type="text" name="quiz2" onChange={this.handleInput} value={this.state.quiz2} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 3.Yazılı</label>
                                            <input type="text" name="quiz3" onChange={this.handleInput} value={this.state.quiz3} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 1.Performans</label>
                                            <input type="text" name="perform1" onChange={this.handleInput} value={this.state.perform1} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 2.Performans</label>
                                            <input type="text" name="perform2" onChange={this.handleInput} value={this.state.perform2} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-1'>
                                            <label> Proje</label>
                                            <input type="text" name="project" onChange={this.handleInput} value={this.state.project} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-1'>
                                            <label> Öğrenci Not Ortalaması: </label>
                                            <span> {this.state.totalNotes}</span>
                                        </div>
                                        <div className='form-group mb-1'>
                                            <button type='button' onClick={this.updateNotes} className='btn btn-primary'>Kaydet</button>
                                        </div>
                                    </form>

                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default EditNotes;
