import axios from 'axios';
import React, {Component} from 'react';
import {Link} from 'react-router-dom';

class Student extends Component{

    state = {
        notes: [],
        loading: true,
    }
    async componentDidMount(){
        const res = await axios.get('http://127.0.0.1:8000/api/notes');

        if(res.data.status === 200){
            this.setState({
                notes: res.data.notes,
                loading: false
            })
        }
        
    }
    render(){

        var notes_HTMLTABLE ="";
        if(this.state.loading){
            notes_HTMLTABLE = <tr><td colSpan="7"><h2>Yükleniyor...</h2></td></tr>
        }
        else{
            notes_HTMLTABLE = 
            this.state.notes.map((item) => {
                return(
                    <tr key={item.tckno}>
                        <td>{item.tckno}</td>
                        <td>{item.quiz1}</td>
                        <td>{item.quiz2}</td>
                        <td>{item.quiz3}</td>
                        <td>{item.perform1}</td>
                        <td>{item.perform2}</td>
                        <td>{item.project}</td>
                        <td>
                            <Link to={'edit-notes/${item.tckno}'} className="btn btn-success btn-sm">Düzenle</Link>
                        </td>
                        <td>
                            <button type='button' className='btn btn-danger btn-sm'>Sil</button>
                        </td>
                        

                    </tr>
                )
            })
        }

        return(
            <div className ="container">
                <div className = "row">
                    <div className ="col-md-12">
                        <div className = "card">
                            <div className = "card-header">
                                <h4>Öğrenci Bilgileri
                                    <Link to={'add-notes'} className="btn btn-primary btn-sm float-end">Not Ekle</Link>
                                </h4>
                                <div className="card-body">
                                        <table className='table table-bordered table-striped'>
                                            <thead>
                                                <tr>
                                                    <th>ID</th>
                                                    <th>Yazılı1</th>
                                                    <th>Yazılı2</th>
                                                    <th>Yazılı3</th>
                                                    <th>Performans1</th>
                                                    <th>Performans2</th>
                                                    <th>Proje</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                            {notes_HTMLTABLE}
                                            </tbody>
                                        </table>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Student;
