import React, { Component } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import queryString from 'query-string';


class EditNotes extends Component {
    state = {
        quiz1: '',
        quiz2: '',
        quiz3: '',
        perform1: '',
        perform2: '',
        project: '',
    }
    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    

    
    async componentDidMount() {
        let params = queryString.parse(this.props.location.search)
        console.log(params);
        
    }

    updateNotes = async (e) => {
        e.preventDefault();

        // const res = await axios.post('http://127.0.0.1:8000/api/add-notes', this.state)

        // if(res.data.status === 200){
        //     this.setState({
        //         quiz1: '',
        //         quiz2: '',
        //         quiz3: '',
        //         perform1: '',
        //         perform2: '',
        //         project: '',
        // });
        // }
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <div className="card">
                            <div className="card-header">
                                <h4>Not Bilgileri
                                    <Link to={'/'} className="btn btn-primary btn-sm float-end">Ana Sayfa</Link>
                                </h4>
                                <div className="card-body">
                                    <form onSubmit={this.updateNotes}>
                                        <div className='form-group mb-3'>
                                            <label> 1.Yazılı</label>
                                            <input type="text" name="quiz1" onChange={this.handleInput} value={this.state.name} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 2.Yazılı</label>
                                            <input type="text" name="quiz2" onChange={this.handleInput} value={this.state.name} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 3.Yazılı</label>
                                            <input type="text" name="quiz3" onChange={this.handleInput} value={this.state.name} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 1.Performans</label>
                                            <input type="text" name="perform1" onChange={this.handleInput} value={this.state.name} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-3'>
                                            <label> 2.Performans</label>
                                            <input type="text" name="perform2" onChange={this.handleInput} value={this.state.name} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-1'>
                                            <label> Proje</label>
                                            <input type="text" name="project" onChange={this.handleInput} value={this.state.name} className="form-control"></input>
                                        </div>
                                        <div className='form-group mb-1'>
                                            <button type='submit' className='btn btn-primary'>Kaydet</button>
                                        </div>
                                    </form>

                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default EditNotes;
